INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (39874, 'Phenobarbital', 'Osilodrostat', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes.', 'DDInter', 'An increase in the dosage of osilodrostat may be required during concomitant use of a potent CYP450 3A4 and/or 2B6 inducer. Dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms. A reduction in dosage of osilodrostat may be required if the potent CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'An increase in the dosage of osilodrostat may be required during concomitant use of a potent CYP450 3A4 and/or 2B6 inducer.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39930/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', NULL, 1767369485);
