INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (44317, 'Biperiden', 'Neostigmine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', 'DDInter', 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44384/', '[1] "Product Information. Mestinon (pyridostigmine)." ICN Pharmaceuticals Inc, Cost Mesa, CA.', 'Orphenadrine', 'Latanoprost, Methazolamide, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, Epinephrine, Brimonidine, Guanethidine, Levobunolol', 1767369485);
