INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (44796, 'Brexpiprazole', 'Bisoprolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44865/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb, Princeton, NJ.[6] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[7] Aronowitz JS, Chakos MH, Safferman AZ, Lieberman JA "Syncope associated with the combination of clozapine and enalapril." J Clin Psychopharmacol 14 (1994): 429-30[8] "Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company, Indianapolis, IN.[9] Fruncillo R, Gibbons W, Vlasses P, Ferguson R "Severe hypotension associated with concurrent clonidine and antipsychotic medication." Am J Psychiatry 142 (1985): 274[10] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[11] White WB "Hypotension with postural syncope secondary to the combination of chlorpromazine and captopril." Arch Intern Med 146 (1986): 1833-4', 'Acetylsalicylic acid, Ivabradine', 'Droperidol, Pimozide, Pimavanserin', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (44797, 'Brigatinib', 'Bisoprolol', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', 'DDInter', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44866/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 'Acetylsalicylic acid, Enalapril, Moexipril, Ramipril, Hydrochlorothiazide, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (44798, 'Brimonidine (ophthalmic)', 'Bisoprolol', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects such as hypotension and bradycardia. The possibility for an additive or potentiating effect on blood pressure and heart rate should be considered when used with other medications that affect these parameters, such as ophthalmic and systemic beta blockers, vasodilators, cardiac glycosides, and antihypertensive agents.', 'DDInter', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs. Patients should be advised to notify their physician if they experience slow pulse, irregular heartbeat, dizziness, lightheadedness, or syncope.', 'Synergism', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/44867/', '[1] "Product Information. Mirvaso (brimonidine topical)." Galderma Laboratories Inc, Cranbury, NJ.[2] Walters TR "Development and use of brimonidine in treating acute and chronic elevations of intraocular pressure: a review of safety, efficacy, dose response, and dosing studies." Surv Ophthalmol 41 ( Suppl (1996): s19-26[3] "Product Information. Alphagan (brimonidine ophthalmic)." Allergan Inc, Irvine, CA.[4] King MH, Richards DW "Near syncope and chest tightness after administration of apraclonidine before argon laser iridotomy." Am J Ophtha