INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46289, 'Bosentan', 'Olaparib', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of olaparib with potent and moderate CYP450 3A4 inducers should generally be avoided. If coadministration with a moderate CYP450 3A4 inducer such as bosentan, dexamethasone, efavirenz, etravirine, modafinil or nafcillin is required, the potential for decreased efficacy of olaparib should be considered.', 'Metabolism', 'Concomitant use of olaparib with potent and moderate CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46359/', '[1] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Porfimer sodium, Niraparib, Selinexor, Aflibercept, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46290, 'Bosentan', 'Oliceridine', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are primarily metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46360/', '[1] "Product Information. Mycobutin (rifabutin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company, Norwalk, CT.[4] "Product Information. Ionsys (fentanyl)." Ortho McNeil Pharmaceutical, Raritan, NJ.[5] "Product Information. Butorphanol Tartrate (butorphanol)." Apotex Corporation, Weston, FL.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[9] Canadian Ph