INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (46761, 'Bosutinib', 'Zafirlukast', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of bosutinib, which is a substrate of the isoenzyme and efflux transporter.', 'DDInter', 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more closely for development of adverse effects such as diarrhea, nausea, vomiting, abdominal pain, myelosuppression, hepatotoxicity, and fluid retention (e.g., pericardial effusion, pleural effusion, pulmonary edema, peripheral edema).', 'Metabolism', 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46831/', '[1] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[6] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 'Theophylline, Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, More', 'Dacomitinib, Pacritinib, Vemurafenib, Vandetanib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, More', 1767369485);
