INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (47995, 'Dutasteride', 'Brigatinib', 'Moderate', 'Coadministration with brigatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Brigatinib has been shown in vitro to induce CYP450 3A4 at clinically relevant plasma concentrations.', 'DDInter', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever brigatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when brigatinib is used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48065/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 'Tamsulosin, Finasteride, Alfuzosin, Terazosin', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetini