INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59135, 'Cholestyramine', 'Spironolactone', 'Moderate', 'Rare cases of severe hyperchloremic, hyperkalemic, metabolic acidosis have been associated with coadministration of spironolactone and cholestyramine. While spironolactone alone can cause metabolic acidosis, the addition of cholestyramine could provide an anion exchange of chloride for bicarbonate in the small bowel. The resulting loss of bicarbonate predisposes the patient to a hyperchloremic, nonanion gap, metabolic acidosis in the presence of renal failure.', 'DDInter', 'If these drugs must be used together, careful monitoring of electrolytes and metabolic status is strongly recommended, particularly in the presence of renal insufficiency.', 'Others', 'If these drugs must be used together, careful monitoring of electrolytes and metabolic status is strongly recommended, particularly in the presence of renal insufficiency.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59211/', '[1] Zapater P, Alba D "Acidosis and extreme hyperkalemia associated with cholestyramine and spironolactone." Ann Pharmacother 29 (1995): 199-200', 'Eplerenone, Amiloride, Triamterene, Finerenone', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Colestipol, More', 1767369485);
