INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59171, 'Edoxaban', 'Spironolactone', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil. This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59247/', '[1] Mendell J, Zahir H, Matsushima N, et al. "Drug-Drug Interaction Studies of Cardiovascular Drugs Involving P-Glycoprotein, an Efflux Transporter, on the Pharmacokinetics of Edoxaban, an Oral Factor Xa Inhibitor." Am J Cardiovasc Drugs (2013):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Savaysa (edoxaban)." Daiichi Sankyo, Inc., Parsippany, NJ.[4] Parasrampuria DA, Mendell J, Shi M, Matsushima N, Zahir H, Truitt K "Edoxaban drug–drug interactions with ketoconazole, erythromycin, and cyclosporine." Br J Cl