INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59344, 'Tacrolimus', 'Spironolactone', 'Major', 'Coadministration with potassium-sparing diuretics may potentiate the risk of hyperkalemia associated with the use of tacrolimus, especially in patients with renal impairment.', 'DDInter', 'Potassium-sparing diuretics should generally be avoided during tacrolimus therapy. Patients treated with tacrolimus should have serum potassium levels and renal function monitored regularly, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Potassium-sparing diuretics should generally be avoided during tacrolimus therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59420/', '[1] "Product Information. Midamor (amiloride)." Merck & Co, Inc, West Point, PA.[2] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[3] Marcy TR, Ripley TL "Aldosterone antagonists in the treatment of heart failure." Am J Health Syst Pharm 63 (2006): 49-58[4] Perazella MA "Drug-induced hyperkalemia: old culprits and new offenders." Am J Med 109 (2000): 307-14[5] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[6] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 'Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Aminobenzoic acid, Ivermectin, Caffeine, Cromoglicic acid, Abrocitinib, Dupilumab, Tralokinumab, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59345, 'Tadalafil', 'Spironolactone', 'Minor', 'Based on their pharmacology, phosphodiesterase-5 (PDE5) inhibitors may conceivably potentiate the hypotensive effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation.', 'DDInter', '-', 'Synergism', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59421/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[3] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[4] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 'Fesoterodine, Phenazopyridine, Acetohydroxamic acid, Trospium, Mirabegron, Yohimbine, Flavoxate, Darifenacin, Papaverine, Ammonium chloride, Dimethyl sulfoxide, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59346, 'Talazoparib', 'Spironolactone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', 'DDInter', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59422/', '[1] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 'Eplerenone, Amiloride, Triamterene, Finerenone', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', 1767369485);
