INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59567, 'Chloroquine', 'Carvedilol', 'Minor', 'In vitro and animal studies have suggested that chloroquine may increase serum levels of some oral beta-blockers. The possible mechanism is inhibition of CYP450 2D6 hepatic metabolism. It is recommended that the patient be monitored for beta-blocker toxicity during coadministration and for beta-blocker efficacy when chloroquine is discontinued. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', '-', 'Metabolism', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59648/', '[1] Somer M, Kallio J, Pesonen U, Pyykko K, Huupponen R, Scheinin M "Influence of hydroxychloroquine on the bioavailability of oral metoprolol." Br J Clin Pharmacol 49 (2000): 549-54[2] "Product Information. Corgard (nadolol)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc, Cost Mesa, CA.[4] Seffart G ed. "Drug Dosage in Renal Insufficiency." Dordrecht, South Holland, : Kluwer Academic Publishers (1991):[5] "Product Information. Betapace (sotalol)." Berlex, Richmond, CA.[6] Lancaster DL, Adio RA, Tai KK, Simooya OO, Broadhead GD, Tucker GT, Lennard MS "Inhibition of metoprolol metabolism by chloroquine and other antimalarial drugs." J Pharm Pharmacol 42 (1990): 267-71[7] Cerner Multum, Inc. "Australian Product Information." O 0', 'Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine', 'Nadolol, Esmolol, Betaxolol, Nebivolol, Bisoprolol, Carteolol, Pindolol, Atenolol, Felodipine, Amlodipine, Nebivolol, More', 1767369485);
