INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59821, 'Carvedilol', 'Phenylephrine (ophthalmic)', 'Moderate', 'A case report suggests that beta-blockers may enhance the pressor response to phenylephrine. The proposed mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of phenylephrine that is responsible for vasoconstriction. Additionally, beta-blockers may desensitize baroreceptors that normally modulate heart rate in response to blood pressure elevations by increasing vagal activity on the sinoauricular node.', 'DDInter', 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Monitoring of blood pressure should be considered, particularly when phenylephrine is administered intravenously or intraocularly.', 'Synergism', 'Until more information is available, caution should be exercised when phenylephrine is used in combination with beta-blockers including ophthalmic formulations, which may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59903/', '[1] Myers MG, Iazzetta JJ "Intranasally administered phenylephrine and blood pressure." Can Med Assoc J 127 (1982): 365-6[2] Cass E, Kadar D, Stein HA "Hazards of phenylephrine topical medication in persons taking propranolol." Can Med Assoc J 120 (1979): 1261-2', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59822, 'Carvedilol', 'Physostigmine', 'Moderate', 'Severe bradycardia and hypotension may develop during concomitant administration of some beta-blockers and anticholinesterase agents. The mechanism is unknown but may involve additive effects.', 'DDInter', 'The patient''s hemodynamic status should be monitored during coadministration. Patients should be advised to notify their physician if they experience syncope, orthostasis, or a slow pulse.', 'Others', 'The patient''s hemodynamic status should be monitored during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59904/', '[1] Eldor J, Hoffman B, Davidson JT "Prolonged bradycardia and hypotension after neostigmine administration in a patient receiving atenolol." Anaesthesia 42 (1987): 1294-7', 'Methazolamide, Acetylcholine, Betaxolol, Diclofenamide, Acetazolamide, Carteolol, Brimonidine, Timolol, Levobunolol, Hydroxocobalamin, Fomepizole, More', 'Felodipine, Acetylsalicylic acid, Amlodipine, Nifedipine', 1767369485);
