INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59901, 'Carvedilol', 'Thiothixene', 'Moderate', 'Some antipsychotic agents and some beta-blockers may mutually inhibit each other''s metabolism resulting in increased or additive pharmacologic effects such as hypotension, bradycardia, delirium or seizures. The mechanism may be related to competitive inhibition of CYP450 2D6-mediated first-pass metabolism.', 'DDInter', 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact. If this combination cannot be avoided, decreased dosage of either or both drugs may be necessary and the patient should be closely monitored for cardiovascular or nervous system adverse effects.', 'Metabolism', 'Atenolol, carteolol, or nadolol may be considered as alternatives because they undergo renal elimination and are not expected to interact.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59983/', '[1] Silver JM, Yudofsky SC, Kogan M, Katz BL "Elevation of thioridazine plasma levels by propranolol." Am J Psychiatry 143 (1986): 1290-2[2] Peet M, Middlemiss D, Yates R "Pharmacokinetic interaction between propranolol and chlorpromazine in schizophrenic patients." Lancet 2 (1980): 978[3] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[4] Miller F, Rampling D "Adverse effects of combined propranolol and chlorpromazine therapy." Am J Psychiatry 139 (1982): 1198-9[5] Vestal RE, Kornhauser DM, Hollifield JW, Shand DG "Inhibition of propranolol metabolism by chlorpromazine." Clin Pharmacol Ther 25 (1979): 19-24', 'Ivabradine', 'Droperidol, Pimozide, Pimavanserin', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59902, 'Carvedilol', 'Thyroid, porcine', 'Minor', 'Thyroid hormone therapy may reverse decreased hepatic blood flow associated with hypothyroidism. Increased hepatic metabolism and decreased serum levels of some beta-blockers may result. Data are available for propranolol only. No special precautions are necessary. When hypothyroidism is converted to a euthyroid state, a decrease in beta-blocking effectiveness is possible.', 'DDInter', '-', 'Others', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59984/', '[1] Aro A, Antila M, Korhonen T, Sundquist H "Pharmacokinetics of propranolol and sotalol in hyperthyroidism." Eur J Clin Pharmacol 21 (1982): 373-7[2] Wells PG, Feely J, Wilkinson GR, Wood AJ "Effect of thyrotoxicosis on liver blood flow and propranolol disposition after long-term dosing." Clin Pharmacol Ther 33 (1983): 603-8[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 'Nebivolol, Felodipine, Amlodipine, Nebivolol, Nifedipine, Ivabradine, Acetylsalicylic acid', NULL, 1767369485);
