INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59926, 'Carvedilol', 'Tyropanoic acid', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', 'DDInter', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60008/', '[1] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[2] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12[3] "Product Information. Visipaque (iodixanol)." Nycomed Inc, Princeton, NJ.[4] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 'Carteolol, Acetylsalicylic acid, Ivabradine', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59927, 'Carvedilol', 'Ubrogepant', 'Moderate', 'Coadministration with breast cancer resistance protein (BCRP) and/or P-glycoprotein (P-gp) only inhibitors may increase the exposure of ubrogepant, which is a substrate of these transporters.', 'DDInter', 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors. If needed, a second 50 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Distribution', 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60009/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 'Acebutolol, Labetalol, Nadolol, Propranolol, Metoprolol, Esmolol, Betaxolol, Nebivolol, Penbutolol, Bisoprolol, Carteolol, More', 'Frovatriptan, Eptinezumab, Rimegepant, Naratriptan, Galcanezumab, Atogepant, Erenumab, Almotriptan, Fremanezumab', 1767369485);
