INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59958, 'Chloramphenicol', 'Cefadroxil', 'Moderate', 'Limited, primarily in vitro, data suggest that chloramphenicol may antagonize the bactericidal activity of cephalosporins against certain clinical isolates of gram-negative rods, group B streptococci, and Staphylococcus aureus. This antagonism appears to occur against strains for which chloramphenicol is bacteriostatic, and has been demonstrated with cefoperazone, cefotaxime, and ceftriaxone. The proposed mechanism is inhibition of protein synthesis by chloramphenicol, resulting in less protein substrate for cephalosporins to act on as inhibitors of bacterial cell wall synthesis.', 'DDInter', 'The manufacturers recommend to avoid concomitant use . However, if concurrent administration cannot be avoided, the possibility of antagonism should be considered, and patients should be monitored for altered therapeutic effect.', 'Antagonism', 'The manufacturers recommend to avoid concomitant use.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60040/', '[1] Brown TH, Alford RH "Antagonism by chloramphenicol of broad-spectrum beta-lactam antibiotics against Klebsiella pneumoniae." Antimicrob Agents Chemother 25 (1984): 405-7[2] Asmar BI, Prainito M, Dajani AS "Antagonistic effect of chloramphenicol in combination with cefotaxime or ceftriaxone." Antimicrob Agents Chemother 32 (1988): 1375-8[3] Agencia Española de Medicamentos y Productos Sanitarios Healthcare "Centro de información online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[4] Brown TH, Alford RH "Failure of chloramphenicol and cefotaxime therapy in Klebsiella meningitis: possible role of antibiotic antago