INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59962, 'Etacrynic acid', 'Cefadroxil', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', 'DDInter', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60044/', '[1] Norrby R, Stenqvist K, Elgefors B "Interaction between cephaloridine and furosemide in man." Scand J Infect Dis 8 (1976): 209-12[2] Chrysos G, Gargalianos P, Lelekis M, Stefanou J, Kosmidis J "Pharmacokinetic interactions of ceftazidime and frusemide." J Chemother 7 Suppl (1995): 107-10[3] Dodds MG, Foord RD "Enhancement by potent diuretics of renal tubular necrosis induced by cephaloridine." Br J Pharmacol 40 (1970): 227-36[4] Simpson IJ "Nephrotoxicity and acute renal failure associated with cephalothin and cephaloridine." N Z Med J 74 (1971): 312-5[5] Korn A, Eichler HG, Gasic S "A drug interaction study of ceftriaxone and 