INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64148, 'Chlorthalidone', 'Topiramate', 'Moderate', 'Coadministration with thiazide-like diuretics, such as hydrochlorothiazide, may enhance the hypokalemic effects and increase the serum concentrations of topiramate. The mechanism for this interaction has not been delineated.', 'DDInter', 'Until more information is available, caution is advised if topiramate is administered with a thiazide-like diuretic, such as hydrochlorothiazide. Dosage adjustments as well as clinical and laboratory monitoring of topiramate should be considered whenever a thiazide-like diuretic is added to or withdrawn from therapy. Serum potassium levels should also be monitored during concomitant therapy. Patients should be observed for the development of topiramate adverse effects.', 'Others', 'Until more information is available, caution is advised if topiramate is administered with a thiazide-like diuretic, such as hydrochlorothiazide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64234/', '[1] "Product Information. Topamax (topiramate)." Ortho Pharmaceutical Corporation, Raritan, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Topiramate (topiramate)." Cipla USA Inc., Miami, FL.', 'Brivaracetam, Ezogabine, Cenobamate, Lamotrigine, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Vemurafenib, Darunavir, Ketoconazole, Probenecid, More', 1767369485);
