INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64667, 'Clomipramine', 'Cholestyramine', 'Minor', 'Cholestyramine and colestipol may interfere with the absorption of oral tricyclic antidepressants, resulting in a moderate reduction of antidepressant serum concentrations. The clinical significance is unknown. The effectiveness of tricyclic antidepressants may be reduced. Monitoring the patient for altered therapeutic effects may be advisable. If an interaction is suspected, tricyclic antidepressants should be administered at least two hours before or four to six hours after cholestyramine or colestipol.', 'DDInter', '-', 'Absorption', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64753/', '[1] Spina E, Avenoso A, Campo GM, Caputi AP, Perucca E "Decreased plasma concentrations of imipramine and desipramine following cholestyramine intake in depressed patients." Ther Drug Monit 16 (1994): 432-4[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bailey DN, Coffee JJ, Anderson B, Manoguerra AS "Interaction of tricyclic antidepressants with cholestyramine in vitro." Ther Drug Monit 14 (1992): 339-42[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, More', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Lovastatin, Clofibrate, Cerivastatin, Colestipol, Niacin, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64668, 'Deferasirox', 'Cholestyramine', 'Moderate', 'Coadministration with cholestyramine may decrease the bioavailability and efficacy of deferasirox. The mechanism involves binding of cholestyramine to deferasirox and its glucuronide metabolites, which are secreted into the bile. This interferes with the enterohepatic recycling of deferasirox by preventing intestinal deconjugation of the glucuronide metabolites and subsequent reabsorption. In healthy volunteers, administration of cholestyramine (12 grams twice daily) 4 and 10 hours after a single dose of deferasirox resulted in a 45% decrease in deferasirox systemic exposure (AUC). The interaction has not been studied with other bile acid sequestrants.', 'DDInter', 'The concomitant use of deferasirox with cholestyramine should generally be avoided. Otherwise, consideration should be given to increasing the initial dosage of deferasirox by 50% with subsequent adjustments made according to serum ferritin levels and clinical response. It is not known whether other bile acid sequestrants interact similarly with deferasirox.', 'Metabolism, Absorption', 'The concomitant use of deferasirox with cholestyramine should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64754/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Ezetimibe, Rosuvastatin, Clofibrate, Niacin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64669, 'Deflazacort', 'Cholestyramine', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', 'DDInter', 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64755/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Pro