INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64676, 'Doxepin', 'Cholestyramine', 'Minor', 'Cholestyramine and colestipol may interfere with the absorption of oral tricyclic antidepressants, resulting in a moderate reduction of antidepressant serum concentrations. The clinical significance is unknown. The effectiveness of tricyclic antidepressants may be reduced. Monitoring the patient for altered therapeutic effects may be advisable. If an interaction is suspected, tricyclic antidepressants should be administered at least two hours before or four to six hours after cholestyramine or colestipol.', 'DDInter', '-', 'Absorption', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64762/', '[1] Spina E, Avenoso A, Campo GM, Caputi AP, Perucca E "Decreased plasma concentrations of imipramine and desipramine following cholestyramine intake in depressed patients." Ther Drug Monit 16 (1994): 432-4[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bailey DN, Coffee JJ, Anderson B, Manoguerra AS "Interaction of tricyclic antidepressants with cholestyramine in vitro." Ther Drug Monit 14 (1992): 339-42[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Clofibrate, Lomitapide, Colestipol, Bempedoic acid, Colesevelam, Alirocumab', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64677, 'Doxercalciferol', 'Cholestyramine', 'Moderate', 'INTERVAL: Bile acid sequestrants and the phosphate binder, sevelamer, can decrease the absorption of fat-soluble vitamins A, D, E, and K. In non-clinical safety studies, rats administered colesevelam at doses greater than 30-fold the projected human clinical dose developed hemorrhage in association with vitamin K deficiency. In a crossover study involving healthy subjects, coadministration of sevelamer with calcitriol resulted in a significant reduction in bioavailability for calcitriol (calcitriol with sevelamer vs calcitriol alone: AUC 137 pg*h/mL vs 318 pg*h/mL and Cmax 40.1 pg/mL vs 49.7 pg/mL, respectively).', 'DDInter', 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Absorption', 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64763/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.[4] Peirce D, Hossack S, Poole L, et.al "The effect of sevelamer carbonate and lanthanum carbonate on the pharmacokinetics of oral calcitriol." Nephrol Dial Transplant 26 (2011): 1615-21[5] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] "Product I