INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64724, 'Cholestyramine', 'Protriptyline', 'Minor', 'Cholestyramine and colestipol may interfere with the absorption of oral tricyclic antidepressants, resulting in a moderate reduction of antidepressant serum concentrations. The clinical significance is unknown. The effectiveness of tricyclic antidepressants may be reduced. Monitoring the patient for altered therapeutic effects may be advisable. If an interaction is suspected, tricyclic antidepressants should be administered at least two hours before or four to six hours after cholestyramine or colestipol.', 'DDInter', '-', 'Absorption', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64810/', '[1] Spina E, Avenoso A, Campo GM, Caputi AP, Perucca E "Decreased plasma concentrations of imipramine and desipramine following cholestyramine intake in depressed patients." Ther Drug Monit 16 (1994): 432-4[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bailey DN, Coffee JJ, Anderson B, Manoguerra AS "Interaction of tricyclic antidepressants with cholestyramine in vitro." Ther Drug Monit 14 (1992): 339-42[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, More', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Simvastatin, Cerivastatin, Lomitapide, Ezetimibe, Niacin, Atorvastatin, Colesevelam, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64725, 'Cholestyramine', 'Quinestrol', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins. The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine. In addition, it has also been suggested that bile-acid binding resins may interfere with the enterohepatic recycling of estrogens and therefore increase their elimination.', 'DDInter', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam. Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64811/', '[1] Underhill KL, Rotter BA, Thompson BK, Prelusky DB, Trenholm HL "Effectiveness of cholestyramine in the detoxification of zearalenone as determined in mice." Bull Environ Contam Toxicol 54 (1995): 128-34[2] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[6] Arts CJM, Govers CARL, van den Berg H, Wolters MGE, van Leeuwen P, Thijssen JHH "In vitro binding of estrogens by dietary fiber and the in vivo apparent digestibility tested in pigs." J Steroid Biochem Mol Biol 38 (1991): 621-8[7] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[8] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', NULL, 1767369485);
