INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64737, 'Cholestyramine', 'Torasemide', 'Moderate', 'INTERVAL: Limited data have shown that the bioavailability of orally administered furosemide is reduced by 95% during concurrent administration of cholestyramine and by 80% during concurrent administration of colestipol. (These figures are based on data from the area under the plasma concentration-time curve and total urinary elimination data.) These decreases in bioavailability were associated with 77% and 58% reductions in the diuretic response to furosemide. No data are available for bumetanide, torsemide, or ethacrynic acid, but a similar interaction is suspected.', 'DDInter', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol. If alternating dosage schedules is ineffective, it may be necessary to increase diuretic dosage during coadministration. Patients should be monitored for altered diuretic efficacy.', 'Absorption', 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64823/', '[1] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Neuvonen PJ, Kivisto K, Hirvisalo EL "Effects of resins and activated charcoal on the absorption of digoxin, carbamazepine and frusemide." Br J Clin Pharmacol 25 (1988): 229-33', 'Tamsulosin, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Bempedoic acid, More', 1767369485);
