INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64757, 'Aluminum hydroxide', 'Choline salicylate', 'Moderate', 'Chronic administration of antacids may reduce serum salicylate concentrations in patients receiving large doses of aspirin or other salicylates. The mechanism involves reduction in salicylate renal tubular reabsorption due to urinary alkalinization by antacids, resulting in increased renal salicylate clearance.', 'DDInter', 'Patients treated chronically with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) and large doses of salicylates (i.e. 3 g/day or more) should be monitored for potentially diminished or inadequate analgesic and anti-inflammatory effects, and the salicylate dosage adjusted if necessary.', 'Excretion', 'Patients treated chronically with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) and large doses of salicylates (i.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64843/', '[1] Gaspari F, Vigano G, Locatelli M, Remuzzi G "Influence of antacid administrations on aspirin absorption in patients with chronic renal failure on maintenance hemodialysis." Am J Kidney Dis 11 (1988): 338-42[2] Covington TR, Lawson LC, Young LL, eds. "Handbook of Nonprescription Drugs. 10th ed." Washington, DC: American Pharmaceutical Association (1993):[3] Levy G, Lampman T, Kamath BL, Garrettson LK "Decreased serum salicylate concentrations in children with rheumatic fever treated with antacid." N Engl J Med 293 (1975): 323-5[4] Brouwers JRBJ, Desmet PAGM "Pharmacokinetic-pharmacodynamic drug interactions with nonsteroidal anti-inflammatory drugs." Clin Pharmacokinet 27 (1994): 462-85