INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64810, 'Iloprost', 'Choline salicylate', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64896/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 'Dipyridamole, Caplacizumab, Cilostazol, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Vorapaxar, Cangrelor, Ticlopidine', 'Ziconotide, Methoxyflurane', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64811, 'Imipramine', 'Choline salicylate', 'Moderate', 'Preliminary evidence suggests that aspirin may increase the plasma free concentration and pharmacologic effects of imipramine by displacing it from plasma protein binding sites.Other salicylates are expected to have similar effects due to their high affinity for plasma proteins.', 'DDInter', 'Pharmacologic response to imipramine should be monitored more closely whenever a salicylate is added to or withdrawn from therapy, and the imipramine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience increased adverse effects of imipramine such as dry mouth, constipation, urinary retention, blurred vision, palpitations, and tachycardia.', 'Distribution', 'Pharmacologic response to imipramine should be monitored more closely whenever a salicylate is added to or withdrawn from therapy, and the imipramine dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64897/', '[1] Juarez-Olguin H, Jung-Cook H, Flores-Perez J, Asseff IL "Clinical Evidence of an Interaction Between Imipramine and Acetylsalicylic Acid on Protein Binding in Depressed Patients." Clin Neuropharmacol 25 (2002): 32-36', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, Trimipramine, More', 'Methoxyflurane', 1767369485);
