INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65189, 'Hemin', 'Cilostazol', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', 'DDInter', 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'The use of hemin with concurrent anticoagulant therapy should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65276/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 'Protein C, Acenocoumarol, Selexipag, Desirudin', 'Betibeglogene autotemcel, Hyaluronidase, Lanadelumab, Human C1-esterase inhibitor, Crizanlizumab, Conestat alfa, Icatibant', 1767369485);
