INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65273, 'Cilostazol', 'Nintedanib', 'Moderate', 'Coadministration of nintedanib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Vascular endothelial growth factor receptor (VEGFR) inhibitors like nintedanib can cause bleeding.', 'DDInter', 'Concomitant use of nintedanib and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended for patients on full anticoagulation therapy, and the anticoagulant dose adjusted as necessary. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of nintedanib and medications that interfere with platelet function or coagulation should be approached cautiously.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65360/', '[1] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.', 'Iloprost, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Treprostinil', 'Dacomitinib, Pexidartinib, Tepotinib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, Axitinib, Ripretinib, Alpelisib, More', 1767369485);
