INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65452, 'Darifenacin', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of darifenacin, which is a substrate of these isoenzymes.', 'DDInter', 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65539/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, Papaverine, Phenyl salicylate, More', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Nizatidine, Esomeprazole, Amoxicillin', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65453, 'Darolutamide', 'Cimetidine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', 'DDInter', '-', 'Metabolism', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65540/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Nizatidine, More', 'Anastrozole, Aminoglutethimide, Letrozole, Fulvestrant, Abarelix, Flutamide, Enzalutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, More', 1767369485);
