INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65572, 'Cimetidine', 'Memantine', 'Minor', 'Theoretically, the concomitant administration of memantine and other drugs that undergo active renal tubular secretion may result in altered plasma levels of both drugs. The clinical significance is unknown. When hydrochlorothiazide/triamterene and memantine were coadministered in a clinical study, the bioavailability of hydrochlorothiazide decreased by 20% and there was no change in memantine or triamterene bioavailability.', 'DDInter', '-', 'Excretion', '-', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65659/', '[1] "Product Information. Namenda (memantine)." Forest Pharmaceuticals, St. Louis, MO.[2] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[3] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Clarithromycin, Vonoprazan, Amoxicillin', 'Ginkgo biloba, Aducanumab', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65573, 'Cimetidine', 'Meperidine', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of meperidine. The proposed mechanism is cimetidine inhibition of meperidine metabolism via hepatic microsomal enzymes.', 'DDInter', 'Caution is advised if meperidine must be used with cimetidine. Patients should be closely monitored for potentially excessive or prolonged central nervous system and respiratory depression. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them. Patients should also be advised to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Alternatively, use of another H2-antagonist such as famotidine, nizatidine, or ranitidine may be considered, as these agents have not been shown to significantly affect CYP450 metabolism.', 'Metabolism', 'Caution is advised if meperidine must be used with cimetidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65660/', '[1] "Product Information. Demerol (meperidine)." Sanofi Winthrop Pharmaceuticals, New York, NY.[2] Guay DR, Meatherall RC, Chalmers JL, Grahame GR, Hudson RJ "Ranitidine does not alter pethidine disposition in man." Br J Clin Pharmacol 20 (1985): 55-9[3] Guay DR, Meatherall RC, Chalmers JL, Grahame GR "Cimetidine alters pethidine disposition in man." Br J Clin Pharmacol 18 (1984): 907-14', 'Naloxone, Naltrexone, Tapentadol', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Vonoprazan, Amoxicillin', 1767369485);
