INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65644, 'Cimetidine', 'Procainamide', 'Moderate', 'Cimetidine may increase plasma concentrations of procainamide and its active metabolite, NAPA. The mechanism is related to decreased renal tubular secretion of procainamide and NAPA. Increased procainamide serum concentrations may result in ECG changes such as QRS widening or prolonged QT interval and increased risk of arrhythmias such as torsades de pointes.', 'DDInter', 'If these two drugs are given together, serum procainamide and NAPA levels, ECG changes, and hemodynamic status should be closely monitored. Patients should be advised to promptly notify their physician if they experience drowsiness, dizziness, syncope, confusion, tremor, or palpitations. Famotidine and nizatidine do not appear to interact in this manner and may be considered as alternatives.', 'Excretion', 'If these two drugs are given together, serum procainamide and NAPA levels, ECG changes, and hemodynamic status should be closely monitored.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65731/', '[1] Rodvold KA, Paloucek FP, Jung D, Gallastegui J "Interaction of steady-state procainamide with H2-receptor antagonists cimetidine and ranitidine." Ther Drug Monit 9 (1987): 378-83[2] Somogyi A, McLean A, Heinzow B "Cimetidine-procainamide pharmacokinetic interaction in man: evidence of competition for tubular secretion of basic drugs." Eur J Clin Pharmacol 25 (1983): 339-45[3] Christian CD, Meredith CG, Speeg KV "Cimetidine inhibits renal procainamide clearance." Clin Pharmacol Ther 36 (1984): 221-7[4] Higbee MD, Wood JS, Mead RA "Procainamide-cimetidine interaction: a potential toxic interaction in the elderly." J Am Geriatr Soc 32 (1984): 162-4[5] Lai MY,