INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65645, 'Cimetidine', 'Propafenone', 'Moderate', 'Cimetidine may inhibit the hepatic metabolism of propafenone. Increased plasma propafenone levels and a significantly increased QRS duration may result. The mechanism is inhibition of CYP450 2D6 metabolism by cimetidine.', 'DDInter', 'Patients receiving this combination should be monitored for altered efficacy and safety of propafenone. If plasma propafenone levels are high or toxicity is suspected, either cimetidine dosage or propafenone dosage should be decreased, or an alternative H2-antagonist (famotidine, nizatidine, ranitidine) should be considered. Patients should be advised to notify their physician if they experience nausea, vomiting, dizziness, blurry vision, palpitations, faster or slower heartbeat, or irregular heartbeat.', 'Metabolism', 'Patients receiving this combination should be monitored for altered efficacy and safety of propafenone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65732/', '[1] Pritchett EL, Smith WM, Kirsten EB "Pharmacokinetic and pharmacodynamic interactions of propafenone and cimetidine." J Clin Pharmacol 28 (1988): 619-24', 'Tinidazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Vonoprazan, Amoxicillin', 'Bretylium, Mexiletine, Ibutilide', 1767369485);
