INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65649, 'Cimetidine', 'Quazepam', 'Moderate', 'Administration of cimetidine with benzodiazepines may increase systemic exposure to benzodiazepines. The mechanism may be related to inhibition of CYP450 3A4 hepatic metabolism by cimetidine, a moderate inhibitor of this isoenzyme. This interaction has been reported for alprazolam, diazepam, triazolam, midazolam and chlordiazepoxide, but may also occur with other benzodiazepines.', 'DDInter', 'The US manufacturers of alprazolam, estazolam and triazolam recommend consideration of a dose reduction when these medicines are co-administered with cimetidine. Use of H2 antagonists and benzodiazepines that are not reported to interact may also be considered. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Metabolism', 'The US manufacturers of alprazolam, estazolam and triazolam recommend consideration of a dose reduction when these medicines are co-administered with cimetidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65736/', '[1] Salonen M, Aantaa E, Aaltonen L, Kanto J "Importance of the interaction of midazolam and cimetidine." Acta Pharmacol Toxicol (Copenh) 58 (1986): 91-5[2] Greenblatt DJ, Abernethy DR, Morse DS, Harmatz JS, Shader RI "Clinical importance of the interaction of diazepam and cimetidine." N Engl J Med 310 (1984): 1639-43[3] Klotz U, Arvela P, Rosenkranz B "Effect of single doses of cimetidine and ranitidine on the steady-state plasma levels of midazolam." Clin Pharmacol Ther 38