INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65677, 'Cimetidine', 'Sildenafil', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of sildenafil. The mechanism is cimetidine inhibition of sildenafil metabolism via CYP450 isoenzymes in the intestinal wall and liver.', 'DDInter', 'Based on the magnitude of the reported interaction, no dosage adjustment for sildenafil should be necessary during coadministration with cimetidine. However, patients should be advised to promptly notify their physician if they experience potential signs and symptoms of sildenafil toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Based on the magnitude of the reported interaction, no dosage adjustment for sildenafil should be necessary during coadministration with cimetidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65764/', '[1] Wilner K, Laboy L, LeBel M "The effects of cimetidine and antacid on the pharmacokinetic profile of sildenafil citrate in healthy male volunteers." Br J Clin Pharmacol 53 Suppl 1 (2002): 31S-6S[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001): 239-48[4] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2000): 392-7[5] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, More', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Esomeprazole, Vonoprazan, Levofloxacin', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65678, 'Cimetidine', 'Silodosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to silodosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the silodosin dosage adjusted as necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Metabolism', 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65765/', '[1] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, More', 'Finasteride, Terazosin', 1767369485);
