INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65973, 'Glycerin', 'Ciprofloxacin', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66060/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 'Tinidazole, Cefepime, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Amikacin, Polymyxin B, Chloramphenicol, Gentamicin, Chlorhexidine, More', 'Alvimopan, Sorbitol, Methylnaltrexone, Plecanatide, Lubiprostone, Methylcellulose, Prucalopride, Linaclotide, Naloxone', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65974, 'Goserelin', 'Ciprofloxacin', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66061/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 'Megestrol acetate, Diethylstilbestrol, Medroxyprogesterone acetate', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Tetracycline, Polymyxin B, Chloramphenicol, Gentamicin, More', 1767369485);
