INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66050, 'Ciprofloxacin', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis. The extent to which other, less potent CYP450 3A4 inhibitors may affect lorlatinib is unknown.', 'DDInter', 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66137/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 'Tetracycline, Norfloxacin, Cefepime, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, Ofloxacin, More', 'Futibatinib, Tucatinib, Regorafenib, Alpelisib, Trametinib, Erdafitinib, Dabrafenib, Nintedanib, Afatinib, Everolimus, Dacomitinib, More', 1767369485);
