INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66052, 'Ciprofloxacin', 'Lumateperone', 'Major', 'Coadministration with strong or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lumateperone, which is metabolized by the isoenzyme in vitro.', 'DDInter', 'Coadministration of strong or moderate CYP450 3A4 inhibitors with lumateperone should be avoided.', 'Metabolism', 'Coadministration of strong or moderate CYP450 3A4 inhibitors with lumateperone should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66139/', '[1] "Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc., New York, NY.[2] "Product Information. Caplyta (lumateperone)." Intra-Cellular Therapies, Inc., New York, NY.', 'Tinidazole, Tetracycline, Cefepime, Norfloxacin, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, More', 'Loxapine, Molindone', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66053, 'Ciprofloxacin', 'Lurasidone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Pharmacologic response to lurasidone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lurasidone dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66140/', '[1] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc, Marlborough, MA.[7] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://ww