INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66668, 'Citalopram', 'Oxitriptan', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'The concomitant use of serotonin reuptake inhibitors and tryptophan should be avoided.', 'Synergism', 'The concomitant use of serotonin reuptake inhibitors and tryptophan should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66755/', '[1] "Product Information. Luvox (fluvoxamine)." Solvay Pharmaceuticals Inc, Marietta, GA.[2] "Product Information. Paxil (paroxetine)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[4] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals, St. Louis, MO.[5] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[6] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.[7] "Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America, Lincolnshire, IL.[8] "Product In