INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (66873, 'Isoniazid', 'Citalopram', 'Major', 'Coadministration with CYP450 2C19 inhibitors may increase the plasma concentrations of citalopram, which is partially metabolized by the isoenzyme. Cases of QT interval prolongation and torsade de pointes have been reported during postmarketing use. In general, the risk of ventricular arrhythmia in association with QT prolongation is largely unpredictable, but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances (e.g., hypokalemia, hypomagnesemia).', 'DDInter', 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole. Alternatives should be considered when possible, and hypokalemia or hypomagnesemia should be corrected prior to initiation of citalopram treatment and periodically monitored. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Given the risk of dose-dependent QT prolongation, citalopram dosage should not exceed 20 mg/day when prescribed in combination with CYP450 2C19 inhibitors such as cimetidine, esomeprazole, etravirine, felbamate, fluconazole, lansoprazole, letrozole, modafinil, omeprazole, oxcarbazepine, ticlopidine, and voriconazole.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66960/', '