INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68012, 'Clarithromycin', 'Telaprevir', 'Moderate', 'Coadministration of a macrolide or ketolide antibiotic (clarithromycin, erythromycin, telithromycin) with a hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) is expected to increase the plasma concentrations of both drugs. The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme. No pharmacokinetic data are currently available. Clinically, high plasma levels of macrolide or ketolide antibiotics may increase the risk of QT interval prolongation and torsade de pointes arrhythmia.', 'DDInter', 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Caution and close clinical monitoring are recommended if clarithromycin, erythromycin, or telithromycin are used in combination with a HCV NS3/4A protease inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68099/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Cimetidine, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Lenacapavir, Velpatasvir, Voxilaprevir, Maribavir, Pibrentasvir, Rimantadine, Elvitegravir, Valganciclovir, More', 1767369485);
