INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68673, 'Picosulfuric acid', 'Ondansetron', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68760/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories, Braintree, MA.[3] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories, Braintree, MA.[4] Hill AG, Parry BR "Hypokalaemia following bowel cleansing with sodium phosphate." N Z Med J 109 (1996): 347[5] "Product Information. Visicol (sodium biphosphate-sodium phosphate)." Salix Pharmaceuticals, Raleigh, NC.[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] "Product Information. Fleet Phospho Soda (sodium biphosphate-sodium phosphate)." Fleet, CB, Lynchburg, VA