INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68713, 'Pexidartinib', 'Magnesium oxide', 'Moderate', 'INTERVAL: Coadministration with acid-reducing agents may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption. The effect of coadministration with an H2-receptor antagonist or locally-acting antacids on pexidartinib pharmacokinetics is unknown.', 'DDInter', 'If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist. Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', 'Absorption', 'If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68800/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 'Magnesium hydroxide, Magnesium gluconate, Sodium sulfate, Zinc sulfate, Magnesium sulfate, Magnesium aspartate, Magnesium lactate, Magnesium citrate, Sodium chloride, Zinc gluconate, Magnesium chloride, More', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, More', 1767369485);
