INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (68781, 'Secnidazole', 'Picosulfuric acid', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate. The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis. The clinical significance of this effect remains unknown.', 'DDInter', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic. If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Others', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/68868/', '[1] "Product Information. Clenpiq (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[2] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[3] "MHRA. Medicines & Healthcare products Regulatory Agency (general site Reference) Available from: URL: http://www.mhra.gov.uk/spc-pil/index.htm."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[6] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[7] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Bisa