INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (70255, 'Clofazimine', 'Ketoconazole', 'Moderate', 'Clofazimine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval. Consider monitoring electrocardiograms, particularly in patients receiving clofazimine dosages greater than 100 mg/day, and discontinue QT-prolonging medications if clinically significant ventricular arrhythmia is noted or if QTcF interval >=500 ms is observed. If syncope occurs, obtain an ECG to detect QT prolongation.', 'Synergism', 'Caution is recommended if clofazimine is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70342/', '[1] "Product Information. Lamprene (clofazimine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', NULL, 1767369485);
