INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (70772, 'Clomipramine', 'Quinestrol', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70859/', '[1] Somani SM, Khurana RC "Mechanism of estrogen-imipramine interaction." JAMA 223 (1973): 560[2] Prange AJ, Wilson IC, Alltop LB "Estrogen may well affect response to antidepressant." JAMA 219 (1972): 143-4[3] Edelbroek PM, Zitman FG, Knoppert-van der Klein EA, van Putten PM, de Wolff FA "Therapeutic drug monitoring of amitriptyline: impact of age, smoking and contraceptives on drug and metabolite levels in bulimic women." Clin Chim Acta 165 (1987): 177-87[4] Krishnan KR, France RD, Ellinwood EH, Jr "Tricyclic-induced akathisia in patients taking conjugated estrogens." Am J Psychiatry 141 (1984): 696-7[5] Abernethy DR, Greenblatt DJ, Shader RI "Imipramine disposition in users of oral contraceptive steroids." Clin Pharmacol Ther 35 (1984): 792-7[6] Khurana RC "Estrogen-imipramine interaction." JAMA 222 (1972): 702-3[7] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids