INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71276, 'Cyclosporine', 'Clonidine', 'Moderate', 'A single case has been reported in which whole-blood cyclosporine levels rose significantly and abruptly after the addition of clonidine and decreased after discontinuation of clonidine. Transdermal clonidine has also been associated with increases in cyclosporine levels. The mechanism is unknown. In addition, it has been suggested that clonidine may have a beneficial effect in preventing cyclosporine-induced nephrotoxicity. The mechanism is unknown and data have been conflicting.', 'DDInter', 'Until more information is available, frequent monitoring of whole-blood cyclosporine levels and renal function is recommended. Patients should be advised to notify their physician if they experience symptoms of possible cyclosporine toxicity, such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, or headache.', 'Others', 'Until more information is available, frequent monitoring of whole-blood cyclosporine levels and renal function is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71363/', '[1] Luke J, Luke DR, Williams LA, LeMaistre CF, Yau JC "Prevention of cyclosporine-induced nephrotoxicity with transdermal clonidine." Clin Pharm 9 (1990): 49-53[2] Gilbert RD, Rahn D, Cassidy M "Interaction between clonidine and cyclosporine A." Nephron 71 (1995): 105[3] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[4] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (19