INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71473, 'Clonidine', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', 'DDInter', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71560/', '[1] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[2] "Product Information. Xywav (calcium/magnesium/potass/sodium oxybates)." Jazz Pharmaceuticals, Palo Alto, CA.[3] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals