INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71587, 'Iloprost', 'Clopidogrel', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71674/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 'Iloprost, Protein C, Acenocoumarol, Selexipag', 'Vorapaxar, Antithrombin III human, Protein C, Clopidogrel', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71588, 'Indomethacin', 'Clopidogrel', 'Moderate', 'The coadministration of clopidogrel with naproxen resulted in occult gastrointestinal blood loss in healthy volunteers. The mechanism has not been described, but may be due to additive platelet inhibition. The coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and clopidogrel should be undertaken with extreme caution. Additionally, some NSAIDs are substrates for the CYP450 isoenzyme 2C9. Clopidogrel inhibits this isoenzyme and therefore may lead to decreased metabolism of these NSAIDs.', 'DDInter', 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel. Patients should also be advised to promptly report any signs of GI bleeding to their caregiver, including pain, swelling, dizziness, weakness, bloody or coffee-ground emesis, or red or black stools, and to avoid any over-the-counter NSAID products.', 'Others', 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71675/', '[1] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 'Fluocinolone acetonide, Fludrocortisone, Betamethasone, Fluorometholone, Chondroitin sulfate, Glucosamine, Phenylbutazone, Fluocinolone acetonide, Cortisone, Betamethasone, Fluorometholone, More', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71589, 'Inotersen', 'Clopidogrel', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function or coagulation may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia.', 'DDInter', 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache.', 'Synergism', 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71676/', '[1] "Product Information. Tegsedi (inotersen)." Akcea Therapeutics, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 'Protein C, Acenocoumarol, Selexipag', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
