INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71657, 'Clopidogrel', 'Sufentanil', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71744/', '[1] Hobl EL, Reiter B, Schoergenhofer C, et al. "Morphine Decreases Ticagrelor Concentrations but not its Antiplatelet Effects: A Randomized Trial in Healthy Volunteers." Eur J Clin Invest 46 (2015): 7-14[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Kubica J, Adamski P, Ostrowska M, et al. "Morphine delays and attenuates ticagrelor exposure and action in patients with myocardial infarction: the randomized, double-blind, placebo-controlled IMPRESSION trial." Eur Heart J (2015):[5] Hobl EL, Stimpfl T, Ebner J, et al. "Morphine Decreases Clopidogrel Concentrations and Effects: A Randomized, Double Blind, Placebo-Controlled Trial." J Am Coll Cardiol (2013):[6] Kubica J, Kubica A, Jilma B, et al. "Impact of morphine on antiplatelet effects of oral P2Y12 receptor inhibitors." Int J Cardiol 215 (2016): 201-208[7