INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71658, 'Clopidogrel', 'Sulindac', 'Moderate', 'The coadministration of clopidogrel with naproxen resulted in occult gastrointestinal blood loss in healthy volunteers. The mechanism has not been described, but may be due to additive platelet inhibition. The coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and clopidogrel should be undertaken with extreme caution. Additionally, some NSAIDs are substrates for the CYP450 isoenzyme 2C9. Clopidogrel inhibits this isoenzyme and therefore may lead to decreased metabolism of these NSAIDs.', 'DDInter', 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel. Patients should also be advised to promptly report any signs of GI bleeding to their caregiver, including pain, swelling, dizziness, weakness, bloody or coffee-ground emesis, or red or black stools, and to avoid any over-the-counter NSAID products.', 'Others', 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71745/', '[1] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 'Chondroitin sulfate, Glucosamine, Phenylbutazone', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
