INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71663, 'Clopidogrel', 'Ticagrelor', 'Moderate', 'The concomitant use of ticagrelor with other platelet inhibitors, anticoagulants, thrombolytic agents, or nonsteroidal anti-inflammatory drugs may increase the risk of bleeding.', 'DDInter', 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71750/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 'Ticagrelor, Protein C, Acenocoumarol, Selexipag, Cangrelor', 'Vorapaxar, Antithrombin III human, Protein C, Clopidogrel', 1767369485);
