INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (71809, 'Clorazepic acid', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71896/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 'Meprobamate, Lorazepam, Oxazepam', 'Entrectinib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Vemurafenib, Selumetinib, More', 1767369485);
