INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79184, 'Daratumumab', 'Smallpox (Vaccinia) Vaccine, Live', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79285/', '[1] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] CDC. Centers for Disease Control and Prevention "Smallpox vaccination clinic guide. Logistical considerations and guidance for state and local planning for emergency, large-scale, voluntary administration of smallpox vaccine in response to a smallpox outbreak. Available from: URL: http://www.bt.cdc.gov" ([2002 Oct 16]):', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', NULL, 1767369485);
