INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (79381, 'Darifenacin', 'Nortriptyline', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many psychotherapeutic drugs such as tricyclic antidepressants, phenothiazines, and other neuroleptics. The mechanism is decreased clearance due to inhibition of the CYP450 2D6 isoenzyme by darifenacin.', 'DDInter', 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever darifenacin is added to or withdrawn from therapy. Clinicians should also be alerted to the potential for additive anticholinergic effects during concomitant administration. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Metabolism', 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79482/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Avanafil, Phenyl salicylate, Magnesium hydroxide, Acetohydroxamic acid, Dimethyl sulfoxide', 'Desvenlafaxine, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Vilazodone, Esketamine', 1767369485);
